import React from 'react';
import ReactDOM from 'react-dom';
import logo from './images/logo.svg';
import styled from 'styled-components';
import { createGlobalStyle } from 'styled-components';

//import UI components
import { Leaderboard } from './components/leaderboardComponent';
import { QuestionsAnswers } from './components/questionAndAnswerComponents';
import { Timer } from './components/TimerComponent';

export const Grid = styled.div``;
export const Row = styled.div`display: flex;`;
export const Col = styled.div`flex: ${(props) => props.size};`;;

export const Button = styled.button`
  float: right;
  background-color: Transparent;
  font-size: 36px;
  font-family: 'Passion One', cursive;
  color: white;
  border: none;
  padding: 12px;
  margin-right: 20px;
  margin-top: 7px;

  :hover {
    text-decoration: underline;
  }
`;

export const HeaderContainer = styled.div`
  color:#E266AC;
  font-family: 'Passion One', cursive;
  font-size: 25px;
  position: relative;
  
`;

export const Title = styled.h1`
  position: relative;
  top: -24px;
`;

export const Logo = styled.img`
  margin: 0.1em;
  width:70px;
  height:70px;
  float: left;
`;

export const HeaderSytle = styled.header`

`;

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Passion+One&display=swap')
    body {
      background-color: #7E549F;
      font-family: 'Passion One', cursive;
    }
`;
const Header = () => {
  return (
    <React.Fragment>
    <GlobalStyle/>
      <HeaderContainer>
        <header>
          <Grid>
            <Row>
              <Col size={0.2}>
                <Logo src={logo} alt="Logo"/>
              </Col>
              <Col size={10}>
                <Title>QUIZSTAR</Title>
              </Col>
              <Col size={1}>
                <Button as="button" type="button">EXIT</Button>
              </Col>
            </Row>
          </Grid>
        </header>
      </HeaderContainer>
    </React.Fragment>
  );
}

//class App extends Component {

  //constructor(props){

 // }
 // render(){

 // }
//// ========================================

ReactDOM.render(
  <Header/>,
  document.getElementById('root')
);

// export default Header;
export default Header;
