import * as React from "react";
import styled from 'styled-components';
import ReactDOM from 'react-dom';
import "./index.css";

export const Grid = styled.div`

`;

export const Row = styled.div`
    display: flex;
`;

export const Col = styled.div`
    flex: ${(props) => props.size};
`;

class App extends React.Component {
  state = {
      
    percent: 15
    
      

  };
  updateProgress = (field, val) => {
    this.setState({ [field]: val });
  };

  render() {
    return (
      <div className="App">
        <div className="div">
        <Grid>
        <Row>
            <Col size={10}>
          <Timer width= 'auto' percent={this.state.percent} />
 </Col>
        <Col size={1}>
        </Col>
            <Col size={1}>
                <div className = "countdown"> {15 -1}</div>
            </Col>
</Row>
</Grid>
        </div>
      </div>
    );
  }
}

export var Timer = ({ width, percent, status }) => {
  const [value, setValue] = React.useState(225);

  React.useEffect(() => {
    //setValue(percent * width);
      value > 0 && setTimeout(() => setValue(value - 15), 1000);
  }, [value]);

  
  return (
    <div className={"progressComp"}>
      <h1 className="percent-number">{status}</h1>
      <div className="progress-div" style={{ width: width }}>
      
                <div className="progress" style={{ width: `${value}px` }}  />
           

      </div>
    </div>
);

}


export default App;

ReactDOM.render(
    <App />,
    document.getElementById('root')
);
